// align blocks height
function initSameHeight() {
	jQuery('.services').sameHeight({
		elements: '.box',
		flexible: true,
		multiLine: true
	});
	jQuery('.two-cols').sameHeight({
		elements: '.graph-box, .rating-box',
		flexible: true,
		multiLine: true
	});
	jQuery('.twocolumns').sameHeight({
		elements: '#sidebar, .content',
		flexible: true,
		multiLine: true
	});
	jQuery('.two-cols').sameHeight({
		elements: '.activity-box, .notification-box',
		flexible: true,
		multiLine: true
	});
	jQuery('.threecolumns').sameHeight({
		elements: '.col',
		flexible: true,
		multiLine: true
	});
	jQuery('.prosnear-newsletter').sameHeight({
		elements: '.nearpros, .news-letter',
		flexible: true,
		multiLine: true
	});
	jQuery('.activity-action').sameHeight({
		elements: '.recent-activity, .action-visual',
		flexible: true,
		multiLine: true
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	jQuery('.carousel .pagination, .popular ul').each(function(){
		new TouchNav({
			navBlock: this
		});
	});
	jQuery('.graph-bars').each(function(){
		new TouchNav({
			navBlock: this,
			menuOpener: 'li',
			menuDrop: 'a'
		});
	});
}

// add classes to support css3 selectors in old browsers
function initChildClasses() {
	jQuery('.notification-icons, .notification-box .box-holder, .faqs .threecolumns').children(':nth-child(2n+1)').addClass('odd');
}

// add class on click
function initAddClasses() {
	jQuery('.add-streetadr').clickClass({
		classAdd: 'add-adress',
		addToParent: 'text-block'
	});
}

